import { ComponentClass } from "react";
import { LinkProps, match } from "react-router-dom";
import * as H from "history";

interface LinkContainerProps extends LinkProps {
    activeClassName?: string;
    activeStyle?: React.CSSProperties;
    exact?: boolean;
    strict?: boolean;
    isActive?(match: match<any>, location: H.Location): boolean;
}

type LinkContainer = ComponentClass<LinkContainerProps>;
declare const LinkContainer: LinkContainer;

export default LinkContainer;
